'use strict';
require(['jquery', 'xwiki-meta'], function ($, xm) {
  var currentReference = xm.documentReference;
  var timelinePageRef = new XWiki.DocumentReference(currentReference.extractReference(XWiki.EntityType.WIKI).getName(), ["ChangeRequest", "Code"], "ChangeRequestTimeline");
  var timelinePageUrl = new XWiki.Document(timelinePageRef).getURL('get');
  var changeRequestId = $('.cr-main-div').attr('data-changerequest-id');

  var refreshTimeline = function() {
    var data = {
      form_token: xm.form_token,
      changerequest: changeRequestId,
      outputSyntax: 'plain'
    };
    $.get(timelinePageUrl, data).done(function (data) {
      var timelineHolder = $('#timeline-content');
      timelineHolder.empty();
      timelineHolder.append(data);
      $(document).trigger('xwiki:changerequest:refreshComments');
      $(document).trigger('xwiki:dom:updated', {'elements': timelineHolder.toArray()});
      $('#timeline-loading').addClass('hidden');
    }).fail(function (data) {
       console.error("Error while trying to refresh the timeline: " + data);
       console.log(data);
       $('#timeline-loading').addClass('hidden');
    });
  }

  $(document).on('xwiki:changerequest:refreshTimeline', function () {
    $('#timeline-loading').removeClass('hidden');
    setTimeout(refreshTimeline, 1000);
  });
});

require(['jquery', 'SaveChangeRequestModal'], function ($, SaveChangeRequestModal) {
  var initializeEditApprovers = function () {
    var saveChangeRequestModal = new SaveChangeRequestModal('changerequest-edit-approvers-config');
    var editApproversLinks = $('a.action_editapprovers').not('.js-augmented');
    editApproversLinks.on('click', function(event) {
      event.preventDefault();
      // FIXME: this is clearly fragile and would need to be based on a proper data-attribute in the future.
      var targetReference = $(event.target).parents('td.cell').siblings().find('ol.breadcrumb').attr('data-entity');
