'use strict';
define('DiffSnippet', ['jquery', 'xwiki-meta'], function ($, xm) {
  const TYPE_ADDED = 'ADDED';
  const TYPE_DELETED = 'DELETED';
  const TYPE_CONTEXT = 'CONTEXT'; // used for diff element only
  const TYPE_UNMODIFIED = 'UNMODIFIED'; // used for chunks only
  const ADD_BEGIN = '<ins>';
  const ADD_END = '</ins>';
  const DEL_BEGIN = '<del>';
  const DEL_END = '</del>';
  const CONTEXT_SIZE = 3;

  var getChunk = function (type, elements) {
    var deleted = type == TYPE_DELETED;
    var added = type == TYPE_ADDED;
    var unmodified = type == TYPE_UNMODIFIED;
    return {
      'type': type,
      'elements': elements.split(""),
      'added': added,
      'deleted': deleted,
      'unmodified': unmodified
    }
  };

  var getDiffElement = function (index, type, value, chunks) {
    var deleted = type == TYPE_DELETED;
    var added = type == TYPE_ADDED;
    return {
      'index': index,
      'type': type,
      'value': value,
      'chunks': chunks,
      'added': added,
      'deleted': deleted
    }
  };

  var getDiffElementType = function (tdLine) {
    if (tdLine.hasClass('diff-line-added')) {
      return TYPE_ADDED;
    } else if (tdLine.hasClass('diff-line-deleted')) {
      return TYPE_DELETED;
    } else {
      return TYPE_CONTEXT;
    }
  };

  var getIndex = function (trLineElement, type) {
    var tdIndexElement;
    if (type == TYPE_CONTEXT || type == TYPE_DELETED) {
      tdIndexElement = $(trLineElement).find('td.diff-line-number').first();
    } else {
      tdIndexElement = $(trLineElement).find('td.diff-line-number').last();
    }
    return parseInt(tdIndexElement.text(), 10) - 1;
  };

  var buildChunks = function (htmlValue) {
    return consumeChunk(htmlValue, []);
  };

  var consumeChunk = function (htmlString, chunkArray) {
    var nextAddition = htmlString.indexOf(ADD_BEGIN);
    var nextDeletion = htmlString.indexOf(DEL_BEGIN);
    if (nextAddition == -1 && nextDeletion == -1) {
      if (chunkArray.length > 0) {
        chunkArray.push(getChunk(TYPE_UNMODIFIED, htmlString));
        return chunkArray;
      } else {
        return null;
      }
    } else if (nextAddition == -1 || (nextDeletion != -1 && nextDeletion < nextAddition)) {
      var indexBegin = nextDeletion;
      var newHtmlString = consumeSingleChunk(htmlString, indexBegin, TYPE_DELETED, chunkArray);
      if (newHtmlString.length > 0) {
        return consumeChunk(newHtmlString, chunkArray);
      } else {
        return chunkArray;
      }
    } else {
      var indexBegin = nextAddition;
      var newHtmlString = consumeSingleChunk(htmlString, indexBegin, TYPE_ADDED, chunkArray);
      if (newHtmlString.length > 0) {
        return consumeChunk(newHtmlString, chunkArray);
      } else {
        return chunkArray;
      }
    }
  }

  var consumeSingleChunk = function (htmlElement, indexBegin, type, chunkArray) {
    if (indexBegin != 0) {
      var contextValue = htmlElement.substring(0, indexBegin);
      chunkArray.push(getChunk(TYPE_UNMODIFIED, contextValue));
    }
    var beginValueIndex;
    var endValueIndex;
    var indexEnd;
    if (type == TYPE_ADDED) {
      beginValueIndex = indexBegin + ADD_BEGIN.length;
      endValueIndex = htmlElement.indexOf(ADD_END);
      indexEnd = endValueIndex + ADD_END.length;
    } else {
      beginValueIndex = indexBegin + DEL_BEGIN.length;
      endValueIndex = htmlElement.indexOf(DEL_END);
      indexEnd = endValueIndex + DEL_END.length;
    }
    chunkArray.push(getChunk(type, htmlElement.substring(beginValueIndex, endValueIndex)));
    return htmlElement.substring(indexEnd);
  }

  var buildDiffElement = function (trLineElement) {
    var tdLine = $(trLineElement).find('td.diff-line');
    var type = getDiffElementType(tdLine);
    var index = getIndex(trLineElement, type);
    // We need to remove first character because it's added to display +/-
    // or space in case of context line
    var value = tdLine.text().substring(1);
    var htmlValue = tdLine.html().substring(1);
    var chunks = buildChunks(htmlValue);
    return getDiffElement(index, type, value, chunks);
  }

  var diffSnippet = function (trLineElement) {
    var diffElements = [buildDiffElement(trLineElement)];
    var currentElement = trLineElement;
    for (i = CONTEXT_SIZE; i > 0; i--) {
      var prevElement = currentElement.prev('tr');
      // If the line contains "diff-line-meta" then it's the first one and we should ignore it.
      if (prevElement.length > 0 && prevElement.has('td.diff-line-meta').length == 0) {
        diffElements.unshift(buildDiffElement(prevElement));
        currentElement = prevElement;
      } else {
        break;
      }
    }
    return diffElements;
  };
  return diffSnippet;
})

