require(['jquery'], function ($) {
  var switchButton = function (element, isExpanded) {
    var heading = $(element).siblings('.panel-heading');
    if (isExpanded) {
      $(heading).find('.button-expand').addClass('hidden');
      $(heading).find('.button-collapse').removeClass('hidden');
    } else {
      $(heading).find('.button-collapse').addClass('hidden');
      $(heading).find('.button-expand').removeClass('hidden');
    }
  };

  var initializeSwitchCollapse = function() {
    $('.diff-panel').on('show.bs.collapse', function () {
      switchButton($(this), true);
    });
    $('.diff-panel').on('hide.bs.collapse', function () {
      switchButton($(this), false);
    });
  };

  (XWiki.isInitialized && initializeSwitchCollapse()) || $(document).on('xwiki:dom:loading', initializeSwitchCollapse);
});

