'use strict';
require(['jquery', 'xwiki-meta'], function ($, xm) {
  var cancelled = false;
  var saveDescription = function (event) {
    if (cancelled) {
      return;
    }
    event.preventDefault();
    $(document).trigger('xwiki:actions:cancel');
    cancelled = true;
    $(this).trigger('click');
  };

  var cancelDescription = function () {
    $(document).trigger('xwiki:actions:cancel');
    window.location.href = new XWiki.Document(xm.documentReference).getURL('view');
  };

  var initialize = function () {
    $("#save-description").on('click', saveDescription);
    $("#cancel-description").on('click', cancelDescription);
  };

  (XWiki.isInitialized && initialize()) || $(document).on('xwiki:dom:loading', initialize);
})

'use strict';
require(['jquery'], function ($) {
  var toggleReply = function () {
    var element = $(this);
    var originalMessageReference = element.attr('data-message-reference');
    var selector = '.message[data-reference="' + originalMessageReference +'"]';
    $('.showMessage').removeClass('showMessage');
    if ($(selector).parents('.collapse:not(.in)').length > 0) {
      $(selector).parents('.collapse:not(.in)').addClass('in');
    }
    $(selector).addClass('showMessage');
    $(selector).one('click', function() {
      $(this).removeClass('showMessage');
    });
  };

  var init = function () {
    $('a[data-message-reference]').on('click', toggleReply);
  };

  init() && $(document).on('xwiki:dom:updated', init);
});

