'use strict';
require(['jquery', 'xwiki-meta', 'ChangeRequestDiscussionHandler', 'CommentEditor'], function ($, xm, DiscussionHandler, CommentEditor) {
  var initReply = function () {
    $('.message .reply-button:visible').one('click', function (event) {
      var changeRequestId = $(this).parents('.cr-main-div').attr('data-changerequest-id');
      var message = $(this).parents('.message');
      var discussion = message.parents('.discussion');
      var discussionType = discussion.attr('data-type');
      var discussionReference = discussion.attr('data-reference');
      var messageReference = message.attr('data-reference');

      var editor = new CommentEditor($(this), {
          'type': 'replyTo',
          'changeRequestId': changeRequestId,
          'originalMessage': messageReference,
          'originalDiscussionType': discussionType,
          'originalDiscussionReference': discussionReference
      });
      editor.requestEditor(event.target);
    });
  };

  var initialize = function () {
    initReply();
    $(document).on('xwiki:dom:updated', initReply);
    $('a[data-toggle="tab"][href="#reviews"]').on('shown.bs.tab', initReply);
    $('.diff-panel').on('shown.bs.collapse', initReply);
  };
  (XWiki.isInitialized && initialize()) || $(document).on('xwiki:dom:loading', initialize);
});

