'use strict';
require(['jquery', 'xwiki-meta'], function ($, xm) {
  var currentReference = xm.documentReference;
  var timelinePageRef = new XWiki.DocumentReference(currentReference.extractReference(XWiki.EntityType.WIKI).getName(), ["ChangeRequest", "Code"], "ChangeRequestTimeline");
  var timelinePageUrl = new XWiki.Document(timelinePageRef).getURL('get');
  var changeRequestId = $('.cr-main-div').attr('data-changerequest-id');

  var refreshTimeline = function() {
    var data = {
      form_token: xm.form_token,
      changerequest: changeRequestId,
      outputSyntax: 'plain'
    };
    $.get(timelinePageUrl, data).done(function (data) {
      var timelineHolder = $('#timeline-content');
      timelineHolder.empty();
      timelineHolder.append(data);
      $(document).trigger('xwiki:changerequest:refreshComments');
      $(document).trigger('xwiki:dom:updated', {'elements': timelineHolder.toArray()});
    }).fail(function (data) {
       console.error("Error while trying to refresh the timeline: " + data);
       console.log(data);
    });
  }

  $(document).on('xwiki:changerequest:refreshTimeline', function () {
    setTimeout(refreshTimeline, 1000);
  });
});

require(['jquery', 'SaveChangeRequestModal'], function ($, SaveChangeRequestModal) {
  var initializeEditApprovers = function () {
    var saveChangeRequestModal = new SaveChangeRequestModal('changerequest-edit-approvers-config');
    var editApproversLinks = $('a.action_editapprovers').not('.js-augmented');
    editApproversLinks.on('click', function(event) {
      event.preventDefault();
      // FIXME: this is clearly fragile and would need to be based on a proper data-attribute in the future.
      var targetReference = $(event.target).parents('td.cell').siblings('td.cell[data-title=Location]').find('ol.breadcrumb').attr('data-entity');
      var changeRequest = $(event.target).parents('div.cr-main-div').attr('data-changerequest-id');
      var data = {
        'changerequest': changeRequest,
        'docChange': targetReference,
        'explicitDocInfo': 1
      };
      saveChangeRequestModal.openModal(data);
    });
    editApproversLinks.addClass('js-augmented');
  };

  // We use a timeout because this is initialized after the afterEntryFetch is sent and before the LD is displayed
  setTimeout(initializeEditApprovers, 500);
  $(document).on('xwiki:livedata:entriesUpdated', initializeEditApprovers);
  // The new livedata entriesUpdated event has been introduced in XWiki 14.8,
  // we still keep the old event listener for backward compatibility.
  $(document).on('xwiki:livedata:afterEntryFetch', function() {
    setTimeout(initializeEditApprovers, 500);
  });
});

require(['jquery', 'xwiki-meta'], function ($, xm) {
  var currentReference = xm.documentReference;
  var renderedDiffPageRef = new XWiki.DocumentReference(currentReference.extractReference(XWiki.EntityType.WIKI).getName(), ["ChangeRequest", "Code"], "ChangeRequestRenderedDiff");
  var renderedDiffPageUrl = new XWiki.Document(renderedDiffPageRef).getURL('get');
  var changeRequestId = $('.cr-main-div').attr('data-changerequest-id');

  var loadRenderedDiff = function (container) {
    var filechangeId = container.attr('data-filechangeid');
    var data = {
      form_token: xm.form_token,
      changerequest: changeRequestId,
      outputSyntax: 'plain',
      filechangeId: filechangeId,
      action: 'htmlDiff'
    };
    var tabPanel = container.find('.tab-pane.html-diff');
    tabPanel.addClass('loading').load(renderedDiffPageUrl, data, function() {
      tabPanel.removeClass('loading');
      $(document).trigger('xwiki:dom:updated', {'elements': tabPanel.toArray()});
    });
  };

  var initializeRenderedDiff = function () {
    $('.changes-body a[data-hint="rendered"]').on('click', function (event) {
      var container = $(event.target).parents('.changes-body');
      if (!container.hasClass('rendered-loaded')) {
        loadRenderedDiff(container);
      }
    });
  };

  (XWiki.isInitialized && initializeRenderedDiff()) || $(document).on('xwiki:dom:loading', initializeRenderedDiff);
});

